    List<MahwousWeb.Shared.Models.Post> posts { get; set; }

    PaginationDTO paginationDTO = new PaginationDTO();
    int totalAmountPages;


    protected async override Task OnInitializedAsync()
    {
        await authorizer.SetAuthorizationAsync();
        await LoadPosts();

    }

    async Task LoadPosts()
    {
        try
        {
            var paginatedResponse = await postRepository.GetPosts(paginationDTO);
            posts = paginatedResponse.Response;
            totalAmountPages = paginatedResponse.TotalAmountPages;

        }
        catch (Exception ex)
        {
            Console.WriteLine("Error: " + ex.Message);
        }
    }

    async Task DeletePost(int postId)
    {
        var post = posts.First(x => x.Id == postId);
        if (await js.Confirm("تأكيد", $"هل أنت متأكد أنك تريد أن تحذف هذا المنشور ؟", SweetAlertMessageType.error))
        {
            await postRepository.Delete(postId);
            await LoadPosts();
        }
    }

    private async Task SelectedPage(int page)
    {
        await LoadPosts();
    }

