        List<QuoteStatus> quotes { get; set; } = new List<QuoteStatus>();

    private int totalAmountPages;

    private QuoteFilter quoteFilter = new QuoteFilter() { RecordsPerPage = 25};


    bool IsLoading { get; set; } = false;

    bool StopLoading = false;





    protected async override Task OnInitializedAsync()
    {
        await authorizer.SetAuthorizationAsync();
        await InitJsListenerAsync();
        await LoadQuotes();
    }



    protected async Task InitJsListenerAsync()
    {
        await JsRuntime.InvokeVoidAsync("ScrollList.Init", "list-end", DotNetObjectReference.Create(this));
    }


    async Task LoadQuotes()
    {
        if (IsLoading)
            return;


        IsLoading = true;
        StateHasChanged();

        try
        {
            var paginatedResponse = await quoteRepository.GetQuotesFiltered(quoteFilter);
            quotes.AddRange(paginatedResponse.Response);
            totalAmountPages = paginatedResponse.TotalAmountPages;
            quoteFilter.Page++;
        }
        catch (Exception ex)
        {
            Console.WriteLine("Error: " + ex.Message);
        }

        IsLoading = false;
        StateHasChanged();

    }

    [JSInvokable]
    public async Task LoadMore()
    {
        if (quoteFilter.Page <= totalAmountPages)
            await LoadQuotes();
        else
            await StopListener();
    }



    public async Task StopListener()
    {
        StopLoading = true;
        IsLoading = false;
        await JsRuntime.InvokeVoidAsync("ScrollList.RemoveListener");
        StateHasChanged();
    }


    public void Dispose()
    {
        JsRuntime.InvokeVoidAsync("ScrollList.RemoveListener");
    }


